function solve(input)
	floor = 0
	position = 1
	for chr in input
		if chr == '('
			floor += 1
		elseif chr == ')'
			floor -= 1
		else
			println("invalid character: $chr")
		end
		if floor < 0
			return position
		end
		position += 1
	end
	println("santa never entered the basement")
	return -1
end

output = solve(input)

println(output)
