sequence = ""

function solve(input)
	mutations = Set{String}()
    regex = r"([A-Za-z]+) => ([A-Za-z]+)"
	for line in split(input, "\n")
		m = match(regex, line)
		start = 1
		nrange = findnext(m[1], sequence, start)
		while nrange != nothing
			push!(mutations, (nrange[1] == 1 ? "" : sequence[1:nrange[1]-1]) *
				m[2] * sequence[nrange[1] + length(nrange):length(sequence)])
			start = nrange[1] + 1
			nrange = findnext(m[1], sequence, start)
		end
	end
	return length(mutations)
end

output = solve(input)

