sequence = ""

tlen(t) = length(t[2])

function solve(input, sequence)
    regex = r"([A-Za-z]+) => ([A-Za-z]+)"
	rs = Vector{Tuple{String,String}}()
	for line in split(input, "\n")
		m = match(regex, line)
		push!(rs, (m[1], m[2]))
	end
	sort!(rs, by=tlen, rev=true)
	modifications = 0
	while sequence != "e"
		r = 1
		while findfirst(rs[r][2], sequence) == nothing
			r += 1
		end
		sequence = replace(sequence, rs[r][2] => rs[r][1], count = 1)
		modifications += 1
	end
	return modifications
end

